import jobCardDetails from '../organisms/RenderJobCards/jobCardDetails';
import { FINDJOB, SAVEJOB } from './types';

const initialState = {
    jobsInFindJobs: jobCardDetails,
    jobsInSavedJobs: [],
};

const jobReducer = (state = initialState, action) => {
    switch(action.type){
        case SAVEJOB:
            return{
                ...state,
                jobsInSavedJobs: [
                    ...state.jobsInSavedJobs,
                    {
                        ...action.job,
                    },
                ]
            };
        default:
            return state;
    }
};

export default jobReducer;
