import jobCardDescription from '../organisms/RenderJobCards/jobCardDescription';
import { SHOWJOB } from './types'

const initialState = {
    jobInShowJob: [],
}

const jobDescriptionReducer = (state = initialState, action) => {
    switch(action.type){
        case SHOWJOB:
            return{

                jobInShowJob: jobCardDescription[action.id],
            };
        default:
            return state;
    }
}

export default jobDescriptionReducer;
