import jobCardDetails from '../organisms/RenderJobCards/jobCardDetails';
import { FINDJOB, SAVEJOB } from './types';

const initialState = {
    jobsInFindJobs: jobCardDetails,
    jobsInSavedJobs: [],
};

const jobReducer = (state = initialState, action) => {
    switch(action.type){
        case FINDJOB:
            return {
                ...state.jobsInFindJobs
            }
        case SAVEJOB:
            const isAvailable = Object.keys(jobsInSavedJobs).length>0 && Array.forEach(jobsInSavedJob => {
                if(jobsInSavedJob.id === action.job.id)
                    return false
            });
            return{
                ...state,
                jobsInSavedJobs: [...state.jobsInSavedJobs,action.job]
            };
        default:
            return state;
    }
};

export default jobReducer;
