import jobCardDetails from '../organisms/RenderJobCards/jobCardDetails';
import { FINDJOB, SAVEJOB } from './types';

const initialState = {
    jobsInFindJobs: jobCardDetails,
    jobsInSavedJobs: [],
};

const jobReducer = (state = initialState, action) => {
    switch(action.type){
        case FINDJOB:
            return {
                ...state.jobsInFindJobs
            }
        case SAVEJOB:
            const len = Object.keys(state.jobsInSavedJobs).length
            const isAvailable = () => { for(var i=0;i<len;i++){
                if(state.jobsInSavedJobs[i].id===action.job.id){
                    return true;
                }
                return false;
            }}
            if(isAvailable){
                    return{
                        state
                    }
                }
                else{
                return{    
                ...state,
                jobsInSavedJobs: [...state.jobsInSavedJobs,action.job]
                }};
        default:
            return state;
    }
};

export default jobReducer;
